mapper.select:
select id, dataset, audioName, time, formerType, latterType
from operationhistory
where user = #{userName}

mapper.delete:
delete
from operationhistory
where dataset = #{dataset}
  and audioName = #{audioName}
  and formerType = #{formerType}
  and latterType = #{latterType}
  and time = #{date}
  and user = #{userName}
